import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {

  useEffect(() => {
    const colors = ['simon-green', 'simon-red', 'simon-yellow', 'simon-blue'];

    const interval = setInterval(() => {
      const randomColor = colors[Math.floor(Math.random() * colors.length)];
      setActiveColor(randomColor);

      // Reset after 500ms to simulate light turning off
      setTimeout(() => setActiveColor(null), 500);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ textAlign: 'center', padding: '50px' }}>
      <h1>Bienvenue sur notre application</h1>
      <p>Veuillez vous connecter ou vous inscrire pour accéder à votre tableau de bord</p>
      <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', marginTop: '20px' }}>
        <Link to="/login" style={{ 
          textDecoration: 'none',
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: 'white',
          borderRadius: '5px'
        }}>
          Se connecter
        </Link>
        <Link to="/register" style={{ 
          textDecoration: 'none',
          padding: '10px 20px',
          backgroundColor: '#28a745',
          color: 'white',
          borderRadius: '5px'
        }}>
          S'inscrire
        </Link>
        <div className='simon'>
          <div className='simon-green'></div>
          <div className='simon-red'></div>
          <div className='simon-yellow'></div>
          <div className='simon-blue'></div>
        </div>
      </div>
    </div>
  );
};

export default Home;
